#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

//====MACRO Define ADC Clock Peripheral===============
#define ADC_CLC_BASE 		0x40012000UL

#define ADC_CLC_OFFSET 		0x04

#define ADC_CR1_REG_ADDR		(ADC_CLC_BASE + ADC_CLC_OFFSET)

//=============macros enable clock=======================
#define RCC_BASE_ADDR 		0x40023800UL

#define RCC_APB2_CLOCK		0x44UL

#define RCC_APB2_CLOCK_ADDR		0x40023844UL


int main(void)
{
	uint32_t *pRCCSetClock = (uint32_t*) RCC_APB2_CLOCK_ADDR;

	uint32_t *p_AddCR1Reg = (uint32_t*) ADC_CR1_REG_ADDR;

	//1. ENABLE CLOCK
	*pRCCSetClock |= (1 << 8);

	// 2. SET TO 1
	*p_AddCR1Reg |= (1 << 8);




}
